mapper.update:
UPDATE t_role_emp
	SET  
		ru_id = #{ruId}
		,emp_id = #{empId}
		,role_id = #{roleId}
	WHERE ru_id = #{ruId}

mapper.select:
select 
		t.ru_id ruId ,
		t.emp_id empId ,
		t.role_id roleId 
 from t_role_emp t where 1=1 

and t.ru_id =#{ruId}


and t.emp_id =#{empId}


and t.role_id =#{roleId}


	limit #{st} , #{r}

select count(*) cnt from t_role_emp t where 1=1 

and t.ru_id =#{ruId}


and t.emp_id =#{empId}


and t.role_id =#{roleId}
