mapper.update:
UPDATE t_asset
	SET  
		asset_id = #{assetId}
		,branch_id = #{branchId}
		,product_id = #{productId}
		,price = #{price}
		,guaratee_term = #{guarateeTerm}
		,check_seq = #{checkSeq}
		,state = #{state}
		,emp_id = #{empId}
		,create_time = #{createTime}
		,create_emp = #{createEmp}
		,sn = #{sn}
		,run_state = #{runState}
	WHERE asset_id = #{assetId}

mapper.select:
select * from v_asset t where 1=1 

and t.asset_id =#{assetId}


and t.branch_id =#{branchId}


and t.product_id =#{productId}


and t.price =#{price}


and t.guaratee_term =#{guarateeTerm}


and t.check_seq =#{checkSeq}


and t.state =#{state}


and t.emp_id =#{empId}


and t.create_time =#{createTime}


and t.create_emp =#{createEmp}


and t.sn =#{sn}


and t.run_state =#{runState}



 order by ${orderby}



	limit #{st} , #{r}

select count(*) cnt from v_asset t where 1=1 

and t.asset_id =#{assetId}


and t.branch_id =#{branchId}


