mapper.update:
UPDATE t_fault
	SET  
		fault_id = #{faultId}
		,asset_id = #{assetId}
		,fault_description = #{faultDescription}
		,fault_time = #{faultTime}
		,vender_id = #{venderId}
		,maintenance_time = #{maintenanceTime}
		,maintenance_detail = #{maintenanceDetail}
		,cost = #{cost}
		,fault_state = #{faultState}
		,operator = #{operator}
		,operator_date = #{operatorDate}
	WHERE fault_id = #{faultId}

mapper.select:
select t.* , a.* , s.state_text fault_state_text from t_fault t , v_asset a , t_state s where 1=1
and t.asset_id = a.asset_id  and t.fault_state = s.state_id  

and t.fault_id =#{faultId}


and t.asset_id =#{assetId}


and t.fault_description =#{faultDescription}


and t.fault_time =#{faultTime}


and t.vender_id =#{venderId}


and t.maintenance_time =#{maintenanceTime}


and t.maintenance_detail =#{maintenanceDetail}


and t.cost =#{cost}


and t.fault_state =#{faultState}


and t.operator =#{operator}


and t.operator_date =#{operatorDate}



and a.branch_id =#{branchId}



 order by #{orderby}



	limit #{st} , #{r}

select count(*) cnt from t_fault t where 1=1 

and t.fault_id =#{faultId}


and t.asset_id =#{assetId}


and t.fault_description =#{faultDescription}


and t.fault_time =#{faultTime}


and t.vender_id =#{venderId}


and t.maintenance_time =#{maintenanceTime}


and t.maintenance_detail =#{maintenanceDetail}


and t.cost =#{cost}


and a.branch_id =#{branchId}



and t.fault_state =#{faultState}


and t.operator =#{operator}


and t.operator_date =#{operatorDate}
