mapper.update:
UPDATE t_product
	SET  
		product_id = #{productId}
		,product_name = #{productName}
		,goodstype_id = #{goodstypeId}
		,brand_id = #{brandId}
	WHERE product_id = #{productId}

mapper.select:
select 
		t.product_id productId ,
		t.product_name productName ,
		t.goodstype_id goodstypeId ,
		t.brand_id brandId ,
		g.goodstype_name goodstypeName , 
		b.brand_name brandName 
 from t_product t , t_goodstype g , t_brand b 
 where 1=1 and t.goodstype_id = g.goodstype_id and t.brand_id = b.brand_id 

and t.product_id =#{productId}


and t.product_name =#{productName}


and t.goodstype_id =#{goodstypeId}


and t.brand_id =#{brandId}


	limit #{st} , #{r}

select count(*) cnt from t_product t where 1=1 

and t.product_id =#{productId}


and t.product_name =#{productName}


and t.goodstype_id =#{goodstypeId}


and t.brand_id =#{brandId}
