mapper.update:
UPDATE t_project
	SET  
		project_id = #{projectId}
		,project_name = #{projectName}
		,year = #{year}
	WHERE project_id = #{projectId}

mapper.select:
select * from t_project t where 1=1 

and t.project_id =#{projectId}


and t.project_name =#{projectName}


and t.year =#{year}



 order by #{orderby}



	limit #{st} , #{r}

select count(*) cnt from t_project t where 1=1 

and t.project_id =#{projectId}


and t.project_name =#{projectName}


and t.year =#{year}
