mapper.update:
UPDATE t_takeback
	SET  
		takeback_id = #{takebackId}
		,asset_id = #{assetId}
		,sn = #{sn}
		,takeback_time = #{takebackTime}
		,operator = #{operator}
		,operator_time = #{operatorTime}
		,user_id = #{userId}
	WHERE takeback_id = #{takebackId}

mapper.select:
select * from t_takeback t where 1=1 

and t.takeback_id =#{takebackId}


and t.asset_id =#{assetId}


and t.sn =#{sn}


and t.takeback_time =#{takebackTime}


and t.operator =#{operator}


and t.operator_time =#{operatorTime}


	limit #{st} , #{r}

select count(*) cnt from t_takeback t where 1=1 

and t.takeback_id =#{takebackId}


and t.asset_id =#{assetId}


and t.sn =#{sn}


and t.takeback_time =#{takebackTime}


and t.operator =#{operator}


and t.operator_time =#{operatorTime}
