mapper.update:
UPDATE t_takeout
	SET  
		takeout_id = #{takeoutId}
		,asset_id = #{assetId}
		,user_id = #{userId}
		,purpose = #{purpose}
		,takeout_time = #{takeoutTime}
		,takeback_time = #{takebackTime}
		,operator = #{operator}
		,operator_time = #{operatorTime}
		,sn=#{sn}
	WHERE takeout_id = #{takeoutId}

mapper.select:
select 
		t.takeout_id takeoutId ,
		t.asset_id assetId ,
		t.user_id userId ,
		t.purpose purpose ,
		t.takeout_time takeoutTime ,
		t.takeback_time takebackTime ,
		t.operator operator ,
		t.operator_time operatorTime ,
		t.sn sn
 from t_takeout t where 1=1 

and t.takeout_id =#{takeoutId}


and t.asset_id =#{assetId}


and t.user_id =#{userId}


and t.purpose =#{purpose}


and t.takeout_time =#{takeoutTime}


and t.takeback_time =#{takebackTime}


and t.operator =#{operator}


and t.operator_time =#{operatorTime}


and t.sn =#{sn}


	limit #{st} , #{r}

select count(*) cnt from t_takeout t where 1=1 

and t.takeout_id =#{takeoutId}


and t.asset_id =#{assetId}


and t.user_id =#{userId}


and t.purpose =#{purpose}


and t.takeout_time =#{takeoutTime}


and t.takeback_time =#{takebackTime}


and t.operator =#{operator}


and t.operator_time =#{operatorTime}


and t.sn =#{sn}
