mapper.update:
UPDATE t_employee
	SET  
		emp_id = #{empId}
		,emp_code = #{empCode}
		,name = #{name}
		,branch_id = #{branchId}
	WHERE emp_id = #{empId}

mapper.select:
select 
		t.emp_id empId ,
		t.emp_code empCode ,
		t.name name ,
		t.branch_id branchId 
 from t_employee t where 1=1 

and t.emp_id =#{empId}


and t.emp_code =#{empCode}


and t.name =#{name}


and t.branch_id =#{branchId}


and t.name like #{vagueText} 


	limit #{st} , #{r}

select count(*) cnt from t_employee t where 1=1 

and t.emp_id =#{empId}


and t.emp_code =#{empCode}


and t.name =#{name}


and t.branch_id =#{branchId}
