mapper.update:
UPDATE t_receive
	SET  
		receive_id = #{receiveId}
		,dist_id = #{distId}
		,emp_id = #{empId}
		,receive_time = #{receiveTime}
	WHERE receive_id = #{receiveId}

mapper.select:
select 
		t.receive_id receiveId ,
		t.dist_id distId ,
		t.emp_id empId ,
		t.receive_time receiveTime 
 from t_receive t where 1=1 

and t.receive_id =#{receiveId}


and t.dist_id =#{distId}


and t.emp_id =#{empId}


and t.receive_time =#{receiveTime}


	limit #{st} , #{r}

select count(*) cnt from t_receive t where 1=1 

and t.receive_id =#{receiveId}


and t.dist_id =#{distId}


and t.emp_id =#{empId}


and t.receive_time =#{receiveTime}
