mapper.update:
UPDATE t_role_function
	SET  
		rf_id = #{rfId}
		,role_id = #{roleId}
		,function_id = #{functionId}
	WHERE rf_id = #{rfId}

mapper.delete:
DELETE FROM t_role_function where 1 = 1 

	and 1 = 2


and rf_id =#{rfId}


and role_id =#{roleId}


and function_id =#{functionId}

mapper.select:
select 
		t.rf_id rfId ,
		t.role_id roleId ,
		t.function_id functionId 
 from t_role_function t where 1=1 

and t.rf_id =#{rfId}


and t.role_id =#{roleId}


and t.function_id =#{functionId}


	limit #{st} , #{r}

select count(*) cnt from t_role_function t where 1=1 

and t.rf_id =#{rfId}


and t.role_id =#{roleId}


and t.function_id =#{functionId}
