mapper.update:
UPDATE t_vender
	SET  
		vender_id = #{venderId}
		,vender_name = #{venderName}
	WHERE vender_id = #{venderId}

mapper.select:
select * from t_vender t where 1=1 

and t.vender_id =#{venderId}


and t.vender_name =#{venderName}



 order by #{orderby}



	limit #{st} , #{r}

select count(*) cnt from t_vender t where 1=1 

and t.vender_id =#{venderId}


and t.vender_name =#{venderName}
