mapper.update:
UPDATE t_function
	SET  
		function_id = #{functionId}
		,function_name = #{functionName}
		,url = #{url}
		,module_id = #{moduleId}
		,icon = #{icon}
	WHERE function_id = #{functionId}

mapper.select:
select 
		t.function_id functionId ,
		t.function_name functionName ,
		t.url url ,
		t.module_id moduleId ,
		t.icon icon ,
		m.module_name moduleName 
 from t_function t , t_module m

 where 1=1 and t.module_id = m.module_id  

and t.function_id =#{functionId}


and t.function_name =#{functionName}


and t.url =#{url}


and t.module_id =#{moduleId}


and t.icon =#{icon}


	limit #{st} , #{r}

select count(*) cnt from t_function t where 1=1 

and t.function_id =#{functionId}


and t.function_name =#{functionName}


and t.url =#{url}


and t.module_id =#{moduleId}


and t.icon =#{icon}
