mapper.update:
UPDATE t_module
	SET  
		module_id = #{moduleId}
		,module_name = #{moduleName}
		,icon = #{icon}
	WHERE module_id = #{moduleId}

mapper.select:
select 
		t.module_id moduleId ,
		t.module_name moduleName ,
		t.icon icon 
 from t_module t where 1=1 

and t.module_id =#{moduleId}


and t.module_name =#{moduleName}


and t.icon =#{icon}


	limit #{st} , #{r}

select count(*) cnt from t_module t where 1=1 

and t.module_id =#{moduleId}


and t.module_name =#{moduleName}


and t.icon =#{icon}
