mapper.update:
UPDATE t_role
	SET  
		role_id = #{roleId}
		,role_name = #{roleName}
	WHERE role_id = #{roleId}

mapper.select:
select 
		t.role_id roleId ,
		t.role_name roleName 
 from t_role t where 1=1 

and t.role_id =#{roleId}


and t.role_name =#{roleName}


	limit #{st} , #{r}

select count(*) cnt from t_role t where 1=1 

and t.role_id =#{roleId}


and t.role_name =#{roleName}
