mapper.update:
UPDATE t_brand
	SET  
		brand_id = #{brandId}
		,brand_name = #{brandName}
	WHERE brand_id = #{brandId}

mapper.select:
select 
		t.brand_id brandId ,
		t.brand_name brandName 
 from t_brand t where 1=1 

and t.brand_id =#{brandId}


and t.brand_name =#{brandName}


	limit #{st} , #{r}

select count(*) cnt from t_brand t where 1=1 

and t.brand_id =#{brandId}


and t.brand_name =#{brandName}
