mapper.update:
UPDATE t_goodstype
	SET  
		goodstype_id = #{goodstypeId}
		,goodstype_name = #{goodstypeName}
	WHERE goodstype_id = #{goodstypeId}

mapper.select:
select 
		t.goodstype_id goodstypeId ,
		t.goodstype_name goodstypeName 
 from t_goodstype t where 1=1 

and t.goodstype_id =#{goodstypeId}


and t.goodstype_name =#{goodstypeName}


	limit #{st} , #{r}

select count(*) cnt from t_goodstype t where 1=1 

and t.goodstype_id =#{goodstypeId}


and t.goodstype_name =#{goodstypeName}
